export function NumberConverter(value, led) {
    const binaryString = toBinaryString(value);
    const start = led * 2; // calculate starting index of slice
    const end = start + 2; // calculate ending index of slice
    const binarySlice = binaryString.slice(start, end); // get two-digit slice
    const decimalValue = parseInt(binarySlice, 2); // convert slice to decimal
    return decimalValue;
  }

  function toBinaryString(num) {
    return parseInt(num, 10).toString(2).padStart(8, '0');
  }
