import React, { useState, createContext } from "react";
import { useMutation, gql } from "@apollo/client";

export const AppContext = createContext(null);

export const EDIT_CLIENT = gql`
  mutation editClient($editClientObject: EditClientInput!) {
    editClient(editClientObject: $editClientObject) {
      id
      state
    }
  }
`;

function App() {
  const [editClient, editClientInfo] = useMutation(EDIT_CLIENT);
  const [bin, setBin] = useState("");

  const handleEdit = () => {
    const editClientObject = {
      id: "63e04dfef3c531d74fbb7919",
      state: 25
    };

    editClient({
      variables: {
        editClientObject
      }
    });
  };

  return (
    <AppContext.Provider value={{ bin, setBin }}>
      <div className="App">
        <button onClick={handleEdit}>Edit Client</button>
        <Grid />
      </div>
    </AppContext.Provider>
  );
}

export default App;
