import './App.css';
import Grid from "./Components/Grid";
import React, {useState, createContext} from "react";
import { useQuery, useMutation, gql } from "@apollo/client";


export const AppContext = createContext(null);


export const EDIT_CLIENT = gql`
  mutation editClient($editClientObject: EditClientInput!) {
    editClient(editClientObject: $editClientObject) {
      id
      state
    }
  }
`;

function App() {
  const { data, loading, error } = useQuery(GET_CLIENTS);
  const [editClient, editClientInfo] = useMutation(EDIT_CLIENT);
  console.log(data?.clients[0].state);
  const [bin, setBin] = useState("");

  const handleEdit = async () => {
    const result = await editClient({
      variables: {
        editClientObject: {
          id: "63dff4134a57605b77f84c2f",
          state: "25"
        }
      }
    });
    console.log(result);
  };


  return (
    <AppContext.Provider value={{bin, setBin}}>
      <div className='App'>
        <Grid />
      </div>
    </AppContext.Provider>
  );
}

export default App;
