const LED = (props) => {
    const [counter, setCounter] = useState(0);

    const increment = () => {
        if(counter < 4)
        {
            setCounter(counter+1);  
        }else 
        {
            setCounter(0);  
        }

    }; 
    if(counter == 0)
    {
        return(
            <div className='LedSquare ledOff' onClick={increment}>
                {counter*25}
        </div>
    );
    }else if(counter = 1)
    {
        return(
            <div className='LedSquare ledon25' onClick={increment}>
                {counter*25}
           </div>
        );  
    }else if(counter = 2)
    {
        return(
            <div className='LedSquare ledon25' onClick={increment}>
                {counter*25}
           </div>
        );  
    }else if(counter = 3)
    {
        return(
            <div className='LedSquare ledon25' onClick={increment}>
                {counter*25}
           </div>
        );  
    }else
    {
        return(
            <div className='LedSquare ledon25' onClick={increment}>
                {counter*25}
           </div>
        );  
    }
};
export default LED;
