import React, { useState } from "react";

const LED = (props) => {
    const [counter, setCounter] = useState(0);

    const increment = () => 
    {
        if(counter < 4 )
        {
            setCounter(counter+1)
        }else{
            setCounter(0)
        }

    }; 
    return(
        <div className='LedSquare' onclick={increment}>
            <button onclick={increment}>
                 {counter}
            </button>

       </div>
    );
};
export default LED;
