import { useState } from "react";

const LED = (props) => {
    const [counter, setCounter] = useState(0);

    const increment = () => {
        if(counter < 4)
        {
            setCounter(counter+1);  
        }else 
        {
            setCounter(0);  
        }

    }; 
    if(counter = 0)
    {
    return(
        <div className='LedSquare off' onClick={increment}>
            {counter}
       </div>
    );
    }

};
export default LED;
